mapper.select:
select
order_id, order_number, order_mode, order_num, order_price, transport, discount, status, user_id, show_id, address_id, createDate, deliveryDate, address, shop_name, shop_img, order1, order2
from xiaomai.t_order


        and order_id = #{orderId}


        and order_number = #{orderNumber}


        and order_mode = #{orderMode}


        and order_num = #{orderNum}


        and order_price = #{orderPrice}


        and transport = #{transport}


        and discount = #{discount}


        and status = #{status}


        and user_id = #{userId}


        and show_id = #{showId}


        and address_id = #{addressId}


        and createDate = #{createdate}


        and deliveryDate = #{deliverydate}


        and address = #{address}


        and shop_name = #{shopName}


        and shop_img = #{shopImg}


        and order1 = #{order1}


        and order2 = #{order2}

select
  order_id, order_number, order_mode, order_num, order_price, transport, discount, status, user_id, show_id, address_id, createDate, deliveryDate, address, shop_name, shop_img, order1, order2
from xiaomai.t_order


        user_id = #{user_id}


        and status = #{status}


order by createDate desc
