mapper.select:
select
  order_id, order_number, order_mode, order_num, order_price, transport, discount, status, user_id, show_id, address_id, createDate, deliveryDate, address
from damai.t_order


        and order_id = #{orderId}


        and order_number = #{orderNumber}


        and order_mode = #{orderMode}


        and order_num = #{orderNum}


        and order_price = #{orderPrice}


        and transport = #{transport}


        and discount = #{discount}


        and status = #{status}


        and user_id = #{userId}


        and show_id = #{showId}


        and address_id = #{addressId}


        and createDate = #{createdate}


        and deliveryDate = #{deliverydate}


        and address = #{address}
